import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    filterText: '',
  });

  // Handle form submission
  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log('Filter Text:', formData.filterText);
    // Implement any action with the formData.filterText, such as a filter function
  };

  // Handle input change
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="dashboard-container">
      <h1>Inventory Management Dashboard</h1>

      {/* Add form for user input */}
      <form onSubmit={handleFormSubmit} className="dashboard-form">
        <label htmlFor="filterText">Filter Items:</label>
        <input
          type="text"
          name="filterText"
          id="filterText"
          value={formData.filterText}
          onChange={handleInputChange}
          placeholder="Enter item name or ID to filter"
        />
        <button type="submit">Apply Filter</button>
      </form>

      <div className="buttons-container">
        <button onClick={() => navigate('/add-item')}>Add Item</button>
        <button onClick={() => navigate('/update-item')}>Update Item</button>
        <button onClick={() => navigate('/remove-item')}>Remove Item</button>
        <button onClick={() => navigate('/display-items-category')}>Display Items by Category</button>
        <button onClick={() => navigate('/display-all-items')}>Display All Items</button>
        <button onClick={() => navigate('/search-item')}>Search Item</button>
        <button onClick={() => navigate('/sort-items')}>Sort Items</button>
        <button onClick={() => navigate('/low-stock-items')}>Display Low Stock Items</button>
      </div>
    </div>
  );
};

export default Dashboard;
